package Array;

public class Arrray6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp []= {78,76,65,70,98,99};
		for (int i =0;i<temp.length;i++)
		{
			for(int j = i+1; j<=j;j++)
			{
				if(temp[i]>temp[j])
				{
					System.out.print(" "+1);
				}
				else
				{
					System.out.print(" "+0);
				}
			}
		}

	}

}
