package WhileLoop;

public class WhilDOWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =1;
		while (n<=10) {
			System.out.println(n);
			n++;
		}
		System.out.println("******************************");
		do {
			System.out.println(n);
			n--;
		}while(n>=1);
	}

}
